package javaPackage2020April;

public class BreakAndContinueloops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		for(int i=10;i>1;i--) {
			
			if(i==5) {
		       
				System.out.println(i);
				// break;
				continue;
			
			          }//if          
			System.out.println(i); // it comes out of if loop and prints only number if it is nt equal to 5 number
} //for  

	
	}//main 
	}//class
