package javaPackage2020April;

public class SwitchLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n=10;
		
		switch(n) { //switch(variable)
		
		case 5:System.out.println(" this is case number 5"); //case casevalue(n): Sysout and follwed break
		break;
		case 9:System.out.println("this is case number 9");
		break;
		case 10:System.out.println("this is case number 10");
		break;
		default:
			System.out.println("case not found");
		}

	}

}
